INSERT INTO "foia"."state_strategic_info" ("id", "state_code", "state_name", "transparency_grade", "response_time_statutory", "response_time_actual", "success_rate_percentage", "primary_law_name", "primary_law_citation", "law_type", "official_portal_url", "unusual_features", "fee_structure_details", "exemption_quirks", "deadline_extensions", "common_obstruction_tactics", "agency_evasion_patterns", "typical_false_exemptions", "appeal_process", "appeal_deadline", "enforcement_mechanisms", "court_precedents", "pro_tips", "red_flags", "success_strategies", "attorney_review_typical", "hostile_agency_indicators", "friendly_agency_indicators", "last_updated", "data_sources", "confidence_level") VALUES
	('3eed59b8-058a-4845-8988-14cb525d3eb1', 'TX', 'Texas', 'B+', '10 business days', '15-20 business days', 72.00, 'Texas Public Information Act', 'Texas Government Code Chapter 552', 'PIA', NULL, '{"AG ruling required for denials","Requestor can be charged for programming time","No fee waiver provision","Strong anti-retaliation protections","Very broad definition of public information"}', NULL, NULL, NULL, '{"Excessive programming time estimates ($500+ for simple searches)","Overly broad \"security\" exemptions on routine records","Delayed AG ruling requests (stalling tactic)","False \"no responsive records\" claims","Improper \"ongoing investigation\" exemptions"}', NULL, NULL, 'Request AG ruling within 10 days of denial. AG has 45 days to rule.', NULL, NULL, NULL, '{"Always request electronic format to minimize programming fees","Challenge any programming estimate over $100 for basic records","Cite specific Gov Code sections in your request","Use certified mail for important requests"}', '{"Agency claims everything is \"security sensitive\"","Massive programming time estimates for simple requests","AG ruling delays beyond 45 days","Agencies asking \"why do you need this?\""}', NULL, false, NULL, NULL, '2025-09-09 02:28:50.729235+00', NULL, 'high'),
	('2ce56e6e-4f22-46b2-9754-8925af724880', 'CA', 'California', 'B', '10 calendar days', '12-15 calendar days', 68.00, 'California Public Records Act', 'Government Code Section 6250 et seq.', 'CPRA', NULL, '{"Right to inspect before copying","Expedited response for media requests","Electronic records preferred by law","Fee waiver available for public interest","Broad definition includes emails and texts"}', NULL, NULL, NULL, '{"Overly broad \"deliberative process\" exemptions","Excessive search time estimates","False \"trade secret\" claims on public contracts","Improper redaction of routine information","Claiming consultation delays with third parties"}', NULL, NULL, 'No formal appeal process - must go to court or ombudsman', NULL, NULL, NULL, '{"Always request inspection before copying","Argue public interest for fee waivers","Challenge \"deliberative process\" claims aggressively","Request electronic format to reduce costs"}', '{"Agency claims \"deliberative process\" on factual records","Excessive redaction of names in public meetings","False \"trade secret\" claims on government contracts","Indefinite \"consultation\" delays"}', NULL, false, NULL, NULL, '2025-09-09 02:28:50.729235+00', NULL, 'high'),
	('8ed9f2e4-d56b-4e8a-9f3a-ef1905f3cfee', 'FL', 'Florida', 'A-', 'Immediate if available', '3-7 calendar days', 78.00, 'Florida Sunshine Law', 'Chapter 119, Florida Statutes', 'SUNSHINE', NULL, '{"Immediate response required if records available","First 15 pages free for non-commercial requests","Very broad definition of public records","Strong enforcement with attorney fees","Sunshine law covers meetings and communications"}', NULL, NULL, NULL, '{"False claims of \"active criminal investigation\"","Improper \"security\" exemptions on routine records","Excessive redaction of personal information","Delay tactics citing vague \"reasonable time\"","Claiming records are \"preliminary\" or \"draft\""}', NULL, NULL, 'Circuit court mandamus action. Attorney fees available if you win.', NULL, NULL, NULL, '{"Emphasize \"immediate\" response requirement","Challenge any delay beyond a few days","Use attorney fee threat for compliance","Request rolling production for large requests"}', '{"Any delay beyond 2-3 days without explanation","Claims that routine records are \"security sensitive\"","Excessive redaction of names in public documents","Agencies asking for \"clarification\" on clear requests"}', NULL, false, NULL, NULL, '2025-09-09 02:28:50.729235+00', NULL, 'high'),
	('aef86232-1418-403a-93cc-b7dd9bf63052', 'NY', 'New York', 'B-', '5 business days', '8-12 business days', 65.00, 'Freedom of Information Law', 'Public Officers Law Article 6', 'FOIL', NULL, '{"Very short initial response deadline","Right to appeal to agency head first","Committee on Open Government provides guidance","Broad \"unwarranted invasion of privacy\" exemption"}', NULL, NULL, NULL, '{"Overly broad privacy exemptions on public officials","False \"inter-agency communications\" privilege claims","Excessive fee estimates for electronic records","Improper \"law enforcement\" exemptions on closed cases","Delay tactics during agency head appeals"}', NULL, NULL, 'Appeal to agency head within 30 days, then Article 78 court proceeding', NULL, NULL, NULL, '{"Always appeal denials to agency head first","Challenge privacy exemptions on public officials","Request Committee on Open Government advisory opinion","Cite specific FOIL precedents in appeals"}', '{"Privacy claims on public employee conduct","Blanket \"law enforcement\" exemptions","Excessive fees for email searches","Agency head appeal delays beyond 10 days"}', NULL, false, NULL, NULL, '2025-09-09 02:28:50.729235+00', NULL, 'high');
